import React from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { useState } from "react";
import { useEffect } from "react";
// import Nav from 'react-bootstrap/Nav';
import Navbar from "react-bootstrap/Navbar";
import Button from "react-bootstrap/esm/Button";
// import Container from 'react-bootstrap/Container';
import Transactions from "./Transactions";

function Navigation() {

  const [transactions, setTransactions] = useState([]);
  useEffect(() => {
    axios.get(`${REACT_APP_API_URL}/transactions`).then((res) => {
      setTransactions(res.data);
    });
  }, [transactions]);

  let totalAmount = transactions
    .map((transactions) => transactions.amount)
    .reduce((a, b) => Number(a) + Number(b), 0);

  return (
    <Navbar
      className="navbar"
      sticky="top"
      bg="primary"
      variant="dark"
      expand="lg"
    >
      <div className="container">
        <Link to="/transactions">
          <Button variant='outline-light'>Bank of 'Murrica</Button>
        </Link>
      <h2>{totalAmount.toFixed(2)}</h2>
        <Link to="/transactions/new">
          <Button variant='outline-light'>New Transaction</Button>
        </Link>
      </div>
    </Navbar>
  );
}

export default Navigation;
