import React from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { useState } from "react";
import { useEffect } from "react";
// import Nav from 'react-bootstrap/Nav';
import Navbar from "react-bootstrap/Navbar";
import Button from "react-bootstrap/esm/Button";
// import Container from 'react-bootstrap/Container';
import Transactions from "./Transactions";

function Navigation() {
  const API = process.env.REACT_APP_API_URL;

  const [transactions, setTransactions] = useState([]);
  useEffect(() => {
    axios.get(`${API}/transactions`).then((res) => {
      setTransactions(res.data);
    });
  }, [transactions]);

  let totalAmount = transactions
    .map((transactions) => transactions.amount)
    .reduce((a, b) => Number(a) + Number(b), 0);

  return (
    <Navbar
      className="navbar"
      sticky="top"
      bg="primary"
      variant="dark"
      expand="lg"
    >
      <div className="container">
        <Link to="/transactions">
          <Button variant="outline-light">Bank of 'Murrica</Button>
        </Link>
        <Nav className="me-auto" id="balance">
          <Nav.Item>Total Amount Available: ${totalAmount.toFixed(2)}</Nav.Item>
        </Nav>
        <Link to="/transactions/new">
          <Button variant="outline-light">New Transaction</Button>
        </Link>
      </div>
    </Navbar>
  );
}

export default Navigation;
